import styled, { keyframes } from "styled-components";

const rotation = keyframes`
    to {
        transform: rotate(360deg);
    }
`

export const Bg = styled.img`
    object-fit: cover;
    width: 200%;
    height: 200%;
    animation: ${rotation} 50s linear infinite;
    position: absolute;
    top: 0;
    left: 0;
    opacity: 0.5;
`
