import styled, { keyframes } from "styled-components";

const rotation = keyframes`
    to {
        transform: rotate(180deg);
    }
`

export const Bg = styled.img`
    width: 400vw;
    height: 400vh;
    object-fit: cover;
    // animation: ${rotation} 25s linear infinite;
    position: absolute;
    top: -50%;
    left: -50;
`
