import React, { useState } from 'react';
import {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import './App.css';
import Background from './components/Background';
import VolumeSlider from './components/VolumeSlider';
import Home from './pages/home';

function App() {
  // pour ouvrir et fermer le slider
  const [isSoundOpen, setIsOpen] = useState(false);
  const [soundVolume, setVolume] = useState(100);

  const toggleSound = () => {
    setIsOpen(!isSoundOpen);
  }

  const changeSoundVolume = (value) => {
    setVolume(value);
    console.log(isSoundOpen);
  }

  setTimeout(function() { 
    if (isSoundOpen) {
      toggleSound();
    }
  }, 5000);

  return (
    <Router>
      <Background />
      <Routes>
        <Route path='/home' element={<Home soundVolume={soundVolume}/>} exact/>
      </Routes>
      <VolumeSlider isSoundOpen={isSoundOpen} toggleSound={toggleSound} changeSoundVolume={changeSoundVolume} />
    </Router>
  );
}

export default App;
