import styled from "styled-components";

export const SliderContainer = styled.div`
    display: flex;
    flex-direction: column;
    position: fixed;
    bottom: 40px;
    right: 60px;
`

export const SliderToggle = styled.img`
    width: ${{ isOpen }};
    cursor: pointer;
    filter: drop-shadow(3px 5px 3px #2A6533);
    transition: 0.1s ease-out;

    &:active {
        transform: scale(0.9);
    }
`
