import styled from "styled-components";

export const SliderContainer = styled.div`
    display: flex;
    flex-direction: column;
    position: fixed;
    bottom: 40px;
    right: 60px;
    //gap: 10px;
`

export const SliderToggle = styled.img`
    width: 40px;
    position: absolute;
    bottom: 0;
    right: 0;
    cursor: pointer;
    // filter: drop-shadow(3px 5px 3px #2A6533);
    transition: 0.2s ease-in;
    transform: scale(${({ isSoundOpen }) => (isSoundOpen ? '0' : '1')});

    &:active {
        transition: 0.1s ease-out;
        transform: scale(0.9);
    }
`

export const SliderSection = styled.div`
    display: flex;
    gap: 5px;
    align-items: center;
    transition: 0.2s cubic-bezier(.85,1.76,.75,.81) ${({ animDelay }) => (animDelay ? animDelay : '0')}s;
    transform: scale(${({ isSoundOpen }) => (isSoundOpen ? '1' : '0')});
`

export const SoundIcon = styled.img`
    width: 30px;
`

export const Slider = styled.input`
    width: 100px;
    -webkit-appearance: none;
    appearance: none;
    height: 5px;
    border-radius: 50px;
    background: #fff;
    outline: none;

    &::-webkit-slider-thumb {
        -webkit-appearance: none;
        height: 12px;
        aspect-ratio: 1;
        border-radius: 50%;
        background: #fff;
        cursor: pointer;
        transition: 0?25s;
    }

    &::-webkit-slider-thumb:hover {
        background: #00000;
    }
`
