import React, { useState } from 'react';
import {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import './App.css';
import Background from './components/Background';
import VolumeSlider from './components/VolumeSlider';
import Home from './pages/home';

function App() {
  // pour ouvrir et fermer le slider
  const [isSoundOpen, setIsOpen] = useState(false);

  let mouseX = 0;
  let mouseY = 0;

  document.addEventListener('mousemove', (e) => {
    mouseX = e.clientX;
    mouseY = e.clientY;
  });

  const toggleSound = () => {
    setIsOpen(!isSoundOpen);
    console.log(isSoundOpen);
  }

  setTimeout(function() { 
    if (isSoundOpen) {
      toggleSound();
    }
  }, 5000);

  return (
    <Router>
      <Background onClick={toggleSound} />
      <Routes>
        <Route path='/home' element={<Home toggleSound={toggleSound} />} exact/>
      </Routes>
      <VolumeSlider isSoundOpen={isSoundOpen} toggleSound={toggleSound} />
    </Router>
  );
}

export default App;
