import { SliderContainer, SliderToggle } from './VolumeSliderElements';

const soundIcon = require("../../images/soundIcon.svg").default;

const VolumeSlider = () => {
  // pour ouvrir et fermer le slider
  const [isSoundOpen, setIsOpen] = useState(false)

  const toggleSound = () => {
    setIsOpen(!isSoundOpen)
  }

  return (
    <SliderContainer>
      <SliderToggle src={soundIcon} isSoundOpen={isSoundOpen} />

      {/* <SliderSection>
