import styled, { keyframes } from "styled-components";

const rotation = keyframes`
    to {
        transform: rotate(360deg);
    }
`

export const Bg = styled.img`
    width: 200%;
    height: 200%;
    object-fit: cover;
    animation: ${rotation} 25s linear infinite;
    position: absolute;
    top: -50%;
    left: -50%;
`
